import streamlit as st
import pandas as pd
import numpy as np

st.title("Hello PIET")
st.subheader("this is our 1st streamlit website")
st.text("welcome to piet with our 1st streamlit website")
st.write("select your favrt spot in PIET")
spot=st.selectbox("Yourfavrt spot in PIET is:",["library","cafeteria","sport complex"])
st.write(f"you have selected {spot} as your favrt spot at PIET")
st.success("enjoy your favrt spot")


st.title("PIET event Registration")

if st.button("PIET Registration"):
    st.write("your registration is successful")
add_club=st.checkbox("join any club ")
if add_club:
    st.write("you have joined the club")
club_type=st.radio("select any club name",["technical","clutural","sports"])
st.write(f"you have selected {club_type} club")

role=st.selectbox("chose your role",["participants","organiser","member"])
st.write(f"you have selected {role} role")

team_size=st.slider("Team size",1,10,5)
st.write(f"you have selected {team_size} team")

st.number_input("how many team members",min_value=1,max_value=10,step=1)

name=st.text_input("enter your name")

if name:
    st.write(f"welcome {name} ,you have joined the club")

st.date_input("select your birthdate")


st.title("PIET Sports Club")

col1,col2 =st.columns(2)
with col1:
    st.header("cricket")
    st.image("data:image/jpeg;base64,",width=200)
    st.button('vote for cricket')
    st.success(f"thanks for voting")

with col2:
    st.header("football")
    st.image("data:image/jpeg;base64,",width=200)
    st.button('vote for football')
    st.success(f"thanks for voting")

name=st.sidebar.text_input("enter your  full name")
favrt_sport=st.sidebar.selectbox("chose your favrt sports",["cricket","football","others"])

st.write(f"thank you {name} for selecting {favrt_sport} as your favorite sport")

file=st.file_uploader("upload your file ",type=["csv"])

if file:
    df=pd.read_csv(file)
    st.subheader("Data file")
    st.dataframe(df)

if file:
    st.subheader("Data file top 55 rows using head() function")
    st.dataframe(df.head(5))

    count=df.groupby("release_year")["type"].count()
    st.bar_chart(count)
